import { useState, useEffect } from "react";
import { Modal, Button, Row, Form, Table, Container } from "react-bootstrap";
import axios from 'axios';
import { useHistory } from "react-router-dom";
import { serverUrl } from '../config/config';
import ImageUploader from "react-images-upload";
import SimpleNavbarComponent from "../components/SimpleNavbar";

const NewProductScreen = (props) => {
    const token =  localStorage.getItem('token')
    const [formData, setFormData] = useState({});
    const [Layouts, setLayouts] = useState([]);

    const handleSubmit = async (event) => {
        event.preventDefault();
        if (Validate()) {
            let config = {
                headers: { Authorization: localStorage.getItem('token') }
            }
            const data = new FormData();
            data.append("name", formData.name)
            data.append("image", formData.image)
            data.append("price", formData.price)
            data.append("category", formData.category)
            data.append("brand", formData.brand)
            data.append("countInStock", formData.countInStock)
            data.append("rating", formData.rating)
            data.append("description", formData.description)

            await axios.post(serverUrl + 'http://localhost:5000/api/products/', data, config).then((res) => {
                if (res.status === 200) {
                    alert('Record Added Successfully');
                    setFormData({});
                }
            }).catch((error) => {
                throw error;
            });
        } else {
            alert('Please Fill required data')
        }
    };

    const Validate = () => {
        const fields = ['name','image', 'price', 'category', 'countInStock','brand', 'rating', 'description'];
        const data = fields.filter((obj) => formData[obj] === undefined || '')
        if (data && data.length) {
            return false
        } else {
            return true
        }
    }

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setFormData(values => ({ ...values, [name]: value }))
    }
    const handleFileChange = (e) => {
        const upload_file = e[0];
        setFormData(values => ({ ...values, image: upload_file }));
    }

   
    return (
        <>
            <div>
              <form className="form" onSubmit={submitHandler}>
              <div>
                <h1>New Product</h1>
              </div>
                            <div>
                            <label htmlFor="name">Name</label>
                                <input
                                    id="name"
                                    type="text"
                                    placeholder="Enter name"
                                    value={formData.name || ""}
                                    onChange={handleChange}
                                />
                            </div>
                            <div>
                            <label htmlFor="price">Price</label>
                            <input
                                    id="price"
                                    type="text"
                                    placeholder="Enter price"
                                    value={formData.price || ""}
                                    onChange={handleChange}
                                />
                            </div>
                            <div>
                            <label htmlFor="imageFile">Image File</label>
                            <input
                                    id="imageFile"
                                    type="file"
                                    label="Choose Image"
                                    onChange={handleFileChange}
                                />  
                            </div>
                            <div>
                            <label htmlFor="category">Category</label>
                            <input
                                    id="category"
                                    type="text"
                                    placeholder="Enter category"
                                    value={formData.category || ""}
                                    onChange={handleChange}
                                />
                            </div>
                            <div>
                            <label htmlFor="brand">Brand</label>
                            <input
                                    id="brand"
                                    type="text"
                                    placeholder="Enter brand"
                                    value={formData.brand || ""}
                                    onChange={handleChange}
                                />
                            </div>
                            <div>
                            <label htmlFor="countInStock">Count In Stock</label>
                            <input
                                    id="countInStock"
                                    type="text"
                                    placeholder="Enter countInStock"
                                    value={formData.countInStock || ""}
                                    onChange={handleChange}
                                />
                            </div>
                            <div>
                            <label htmlFor="rating">Rating</label>
                            <input
                                    id="rating"
                                    type="text"
                                    placeholder="Enter rating"
                                    value={formData.rating || ""}
                                    onChange={handleChange}
                                />
                            </div>
                            <div>
                            <label htmlFor="description">Description</label>
                            <input
                                    id="description"
                                    type="text"
                                    placeholder="Enter description"
                                    value={formData.description || ""}
                                    onChange={handleChange}
                                />
                            </div>
                            <div>
                                <label></label>
                                <button className="primary" type="submit">
                                    Save
                                </button>
                            </div>
                        </form>                           
                   
            </div>
        </>
    )
}

export default NewProductScreen;
